import React from 'react'
import {Container, Row, Button} from 'reactstrap' // importing bootstrap lib.
import { Link, NavLink } from 'react-router-dom'

import logo from '../../assets/images/logo.png'

//displaying navigation bars in different pages
const nav__links = [
  {
    path:'/home',
    display:'Home'
    
  },
  {
    path:'#',
    display:'About'
  },
  {
    path:'/tours',
    display:'Tours'
  },
]

const Header = () => {
  return <header className="header">
    <Container>
      <Row>
        <div className="nav__wrapper d-flex align-items-center
        justify-content-between">

          {/*=========== logo ============== */}
          <div className="logo"></div>
          <img src={logo} alt=""/>

          {/*=========== logo end ============== */}

          {/*=========== menu start ============== */}
          <ul className="menu d-flex align-items-center gap-5" >
            {
              nav__links.map((item, index) =>
              ( <li className='nav__item' key={index}>
                <NavLink to={item.path}>{item.display}</NavLink> {/*=========== navigation bar items home, about=========== */}

              </li>
                )
              )
            }

          </ul>
          {/*=========== menu end ============== */}
          
          <div className="nav__right d-flex align-items-center gap-4">
            <div className="nav__btns d-flex align-items-center gap-4">
              <Button className="btn primary__btn"><Link to='/login'>
                Login</Link></Button>
            </div>
          </div>
        </div>
      </Row>
    </Container>
  </header>
}

export default Header
